mapper.select:
select id, user_name, user_password, user_age, user_sex, org_id
from t_user
where user_name=#{userName}

select id, user_name, user_password, user_age, user_sex, org_id
from t_user
where id=#{id}

select id, user_name, user_password, user_age, user_sex, org_id
from t_user
where user_age=#{age}

select id, user_name, user_password, user_age, user_sex, org_id
from t_user
where user_sex=#{userSex}
and user_name=#{userName}

select id, user_name, user_password, user_age, user_sex, org_id
from t_user
where 1=1

    and id = #{userQueryDto.id}


    and id = #{userQueryDto.name}


    and id = #{userQueryDto.password}


    and id = #{userQueryDto.age}


    and id = #{userQueryDto.sex}


    and id = #{userQueryDto.orgId}

select id, user_name, user_password, user_age, user_sex, org_id
from t_user
where 1=1

    and id = #{userQueryByPageDTO.id}


    and id = #{userQueryByPageDTO.name}


    and id = #{userQueryByPageDTO.password}


    and id = #{userQueryByPageDTO.age}


    and id = #{userQueryByPageDTO.sex}


    and id = #{userQueryByPageDTO.orgId}

order by update_time desc
